    public static function convert_date_from_db_for_view($date,$format=NULL) {
        if (!is_string($date) || trim($date) == "") {
            return "";
        }
        $temp = explode(" ", $date);
        $temp = $temp[0];
        $temp = explode("-", $temp);
        if($format==NULL){
            $format=DATE_FORMAT_FOR_VIEW;
        }        
        if($format=="d-m-y"){
            return $temp[2] . '/' . $temp[1] . '/' . $temp[0];
        }
        else if($format=="y-m-d"){
            return $temp[0] . '/' . $temp[1] . '/' . $temp[1];
        }
        return "";
    }
    public static function convert_date_for_saving_db($date,$format=NULL) {
        if (!is_string($date) || trim($date) == "") {
            return "0000-00-00 00:00:00";
        }        
        $temp = explode("/", $date);
        if($format==NULL){
            $format=DATE_FORMAT_FOR_VIEW;
        }        
        if($format=="d-m-y"){
            return $temp[2] . '-' . $temp[1] . '-' . $temp[0]. ' 01:01:01';
        }
        else if($format=="y-m-d"){
            return $temp[0] . '-' . $temp[1] . '-' . $temp[1]. ' 01:01:01';
        }
        return "0000-00-00 00:00:00";
    }

