    /*
      average cost per day
     */

    public static function avg_day($total_cost, $num_date) {
        if ($num_date) {
            $result = $total_cost / $num_date;
            return $result;
        }
        return '';
    }

    /*
      average cost per day of a person
     */

    public static function avg_day_person($total_cost, $num_date, $number_member) {
        if ($num_date && $number_member) {
            $result = ($total_cost / $num_date) / $number_member;
            return $result;
        }

        return '';
    }

    /*
      differene cost in a day
     */

    public static function dif_day($before, $after) {
        return $after - $before;
    }

